import React, { useState, useEffect } from 'react';
import { View, Text, SafeAreaView, ScrollView } from 'react-native';
import { headers, basicStyles } from 'styles';
import { colors } from 'styles';
import Graph from '../components/Graph';
import { useDispatch, useSelector } from 'react-redux';
import { stateIsLoaded } from '../../services/stateHelpers';
import { getStatistics } from '../../redux/actions/data.actions';
import QuickStats from '../components/QuickStats';
import PieChartComponent from '../components/PieChart';

export default function StatisticsScreen({ history }) {
    const dispatch = useDispatch();
    useEffect(() => {
        dispatch(getStatistics());
    }, [dispatch]);
    const statisticsState = useSelector(state => state.statistics);

    if (!stateIsLoaded(statisticsState)) {
        return <View></View>;
    }

    const statisticsData = statisticsState.data.data;
    return (
        <SafeAreaView style={{ flex: 1, marginLeft: -21, zIndex: -1 }}>
            <ScrollView style={[basicStyles.scrollView]}>
                <View style={{ flex: 1 }}>
                    <View style={{ flex: 1, backgroundColor: 'white', alignItems: 'center' }}>
                        <Text style={[headers.H1(colors.lightText(), 'Light', 35), { marginTop: 50, marginBottom: 50, textAlign: 'center' }]}>
                            Statistics
                        </Text>
                        <Graph data={statisticsData.graph} />
                        <QuickStats data={statisticsData}></QuickStats>
                        <PieChartComponent data={statisticsData.totalSubsPerWorkout} />
                    </View>
                </View>
            </ScrollView>
        </SafeAreaView>
    );
}
